package com.doctorkernel.user.core.infra.ddbb.mongo.data;import com.doctorkernel.user.core.domain.valueobjects.Account;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.mapping.Document;@Data@NoArgsConstructor@AllArgsConstructor@Builder@Document (collection= "users")public class UserData {    @Id    private String id;    private String firstName;    private String lastname;    private String emailAddress;    private Account account;}